      res.json({ 
        message: `${alerts.length} alerte(s) de factures √©chues g√©n√©r√©e(s)`,
        alerts 
      });
    } catch (error) {
      console.error("Error generating overdue alerts:", error);
      res.status(500).json({ message: "Erreur lors de la g√©n√©ration des alertes de factures √©chues" });
