import passport from "passport";
import { Strategy as LocalStrategy } from "passport-local";
import { Express } from "express";
import session from "express-session";
import rateLimit from "express-rate-limit";
import { scrypt, randomBytes, timingSafeEqual } from "crypto";
import { promisify } from "util";
import { storage } from "./storage";
import type { User as UserType } from "@shared/schema";

declare global {
  namespace Express {
    interface User extends UserType {}
    interface Request {
      session: session.Session & Partial<session.SessionData>;
    }
  }
}

declare module 'express-session' {
  interface SessionData {
    activatedLicenseKey?: string;
  }
}

const scryptAsync = promisify(scrypt);

async function hashPassword(password: string) {
  const salt = randomBytes(16).toString("hex");
  const buf = (await scryptAsync(password, salt, 64)) as Buffer;
  return `${buf.toString("hex")}.${salt}`;
}

async function comparePasswords(supplied: string, stored: string) {
  const [hashed, salt] = stored.split(".");
  const hashedBuf = Buffer.from(hashed, "hex");
  const suppliedBuf = (await scryptAsync(supplied, salt, 64)) as Buffer;
  return timingSafeEqual(hashedBuf, suppliedBuf);
}

// Hash factice pour √©viter les timing attacks lors de l'√©num√©ration d'utilisateurs
// G√©n√©r√© avec scrypt(keylen=64) pour avoir la bonne longueur (128 hex chars)
const DUMMY_HASH = "0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef0123456789abcdef.0123456789abcdef0123456789abcdef0123456789abcdef";

export function setupAuth(app: Express) {
  // Configuration de la limitation de taux pour les tentatives de connexion
  const loginLimiter = rateLimit({
    windowMs: 15 * 60 * 1000, // 15 minutes
    max: 5, // 5 tentatives par IP par fen√™tre de 15 minutes
    message: {
      message: "Trop de tentatives de connexion depuis cette adresse IP. R√©essayez dans 15 minutes."
    },
    standardHeaders: true,
    legacyHeaders: false,
    handler: (req, res) => {
      res.status(429).json({
        message: "Trop de tentatives de connexion depuis cette adresse IP. R√©essayez dans 15 minutes."
      });
    }
  });

  // Simple session configuration using memory store
  if (!process.env.SESSION_SECRET) {
    throw new Error("SESSION_SECRET environment variable is required");
  }

  const sessionSettings: session.SessionOptions = {
    secret: process.env.SESSION_SECRET,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === 'production', // S√©curis√© en production seulement
      maxAge: 7 * 24 * 60 * 60 * 1000, // 1 week
    },
  };

  app.use(session(sessionSettings));
  app.use(passport.initialize());
  app.use(passport.session());

  // Local strategy
  passport.use(
    new LocalStrategy(
      {
        usernameField: "email",
        passwordField: "password",
      },
      async (email, password, done) => {
        try {
          // V√©rifier l'utilisateur et l'√©tat de verrouillage en une seule requ√™te
          const { user, isLocked } = await storage.checkUserForLogin(email);

          if (isLocked) {
            // Effectuer une comparaison factice pour √©viter les timing attacks
            await comparePasswords(password, DUMMY_HASH);
            return done(null, false, { 
              message: "Email ou mot de passe incorrect" // Message g√©n√©rique pour √©viter l'√©num√©ration
            });
          }

          // Incr√©menter les tentatives pour TOUS les emails, existants ou non (√©vite l'√©num√©ration)
          let shouldLock = false;
          let attemptsCount = 0;

          if (!user || !user.password) {
            // Incr√©menter les tentatives pour TOUS les emails (√©vite l'√©num√©ration)
            await storage.incrementLoginAttempts(email);
            // Effectuer une comparaison factice pour √©viter les timing attacks
            await comparePasswords(password, DUMMY_HASH);
            return done(null, false, { message: "Email ou mot de passe incorrect" });
          }

          const isValid = await comparePasswords(password, user.password);
          if (!isValid) {
            // Incr√©menter les tentatives d'√©chec
            await storage.incrementLoginAttempts(email);

            // V√©rifier si on doit verrouiller le compte (3 tentatives)
            attemptsCount = (user.loginAttempts || 0) + 1;
            if (attemptsCount >= 3) {
              await storage.lockAccount(email, 30); // Verrouiller pendant 30 minutes
              shouldLock = true;
            }

            // Message g√©n√©rique pour √©viter la fuite d'informations
            return done(null, false, { 
              message: "Email ou mot de passe incorrect" 
            });
          }

          // Connexion r√©ussie : r√©initialiser les tentatives
          await storage.resetLoginAttempts(email);
          return done(null, user);
        } catch (error) {
          return done(error);
        }
      }
    )
  );

  passport.serializeUser((user: any, done) => done(null, user.id));

  passport.deserializeUser(async (id: string, done) => {
    try {
      const user = await storage.getUser(id);
      done(null, user);
    } catch (error) {
      done(error);
    }
  });

  // Registration route
  app.post("/api/register", async (req, res, next) => {
    try {
      const { email, password, firstName, lastName } = req.body;

      if (!email || !password || !firstName || !lastName) {
        return res.status(400).json({ 
          message: "Email, mot de passe, pr√©nom et nom sont requis" 
        });
      }

      if (password.length < 6) {
        return res.status(400).json({ 
          message: "Le mot de passe doit contenir au moins 6 caract√®res" 
        });
      }

      const existingUser = await storage.getUserByEmail(email);
      if (existingUser) {
        return res.status(400).json({ 
          message: "Un compte avec cet email existe d√©j√†" 
        });
      }

      const hashedPassword = await hashPassword(password);
      const userId = randomBytes(16).toString("hex");

      const newUser = await storage.createLocalUser({
        id: userId,
        email,
        password: hashedPassword,
        firstName,
        lastName,
      });

      req.login(newUser, async (err) => {
        if (err) return next(err);

        try {
          // Check if there's a pending license activation in session
          if (req.session && req.session.activatedLicenseKey) {
            await storage.setUserLicenseActivated(newUser.id, true);
            // Clear the license from session
            delete req.session.activatedLicenseKey;
          }
        } catch (error) {
          console.error("Error associating license with new user:", error);
          // Continue registration even if license association fails
        }

        res.status(201).json({
          id: newUser.id,
          email: newUser.email,
          firstName: newUser.firstName,
          lastName: newUser.lastName,
        });
      });
    } catch (error) {
      console.error("Erreur lors de l'inscription:", error);
      res.status(500).json({ message: "Erreur serveur lors de l'inscription" });
    }
  });

  // Login route avec limitation de taux
  app.post("/api/login", loginLimiter, (req, res, next) => {
    passport.authenticate("local", (err: any, user: any, info: any) => {
      if (err) {
        return res.status(500).json({ message: "Erreur serveur" });
      }
      if (!user) {
        return res.status(401).json({ 
          message: info?.message || "Email ou mot de passe incorrect" 
        });
      }

      req.login(user, async (err) => {
        if (err) {
          return res.status(500).json({ message: "Erreur de connexion" });
        }

        try {
          // Check if there's a pending license activation in session
          if (req.session && req.session.activatedLicenseKey && !user.licenseActivated) {
            await storage.setUserLicenseActivated(user.id, true);
            // Clear the license from session
            delete req.session.activatedLicenseKey;
          }
        } catch (error) {
          console.error("Error associating license with user:", error);
          // Continue login even if license association fails
        }

        res.json({
          id: user.id,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
        });
      });
    })(req, res, next);
  });

  // Logout route
  app.post("/api/logout", (req, res, next) => {
    req.logout((err) => {
      if (err) return next(err);
      res.json({ message: "D√©connexion r√©ussie" });
    });
  });

  // Get current user route
  app.get("/api/user", async (req, res) => {
    if (!req.isAuthenticated() || !req.user) {
      return res.status(401).json({ message: "Non authentifi√©" });
    }

    try {
      // Always fetch fresh user data from database to get current license status
      const user = await storage.getUser(req.user.id);
      if (!user) {
        return res.status(401).json({ message: "Utilisateur non trouv√©" });
      }

      res.json({
        id: user.id,
        email: user.email,
        firstName: user.firstName,
        lastName: user.lastName,
        phone: user.phone,
        company: user.company,
        position: user.position,
        address: user.address,
        businessType: user.businessType,
        profileImageUrl: user.profileImageUrl,
        licenseActivated: user.licenseActivated, // Include license status
        currency: user.currency,
        language: user.language,
      });
    } catch (error) {
      console.error("Error fetching user data:", error);
      res.status(500).json({ message: "Erreur lors de la r√©cup√©ration des donn√©es utilisateur" });
    }
  });

}

export function isAuthenticated(req: any, res: any, next: any) {
  if (req.isAuthenticated()) {
    return next();
  }
  res.status(401).json({ message: "Authentification requise" });
}

