export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: NewUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async createLocalUser(userData: NewUser): Promise<User> {
    const [user] = await db.insert(users).values(userData).returning();
    return user;
  }

  async updateUserProfile(id: string, profileData: Partial<User>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({
        ...profileData,
        updatedAt: new Date(),
      })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async updateUserSettings(id: string, settings: { currency?: string; language?: string }): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...settings, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async setUserLicenseActivated(id: string, activated: boolean): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ licenseActivated: activated, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  // M√©thodes pour la gestion des tentatives de connexion et verrouillage
  async incrementLoginAttempts(email: string): Promise<void> {
    // Approche simplifi√©e : incr√©menter seulement si l'utilisateur existe
    // Pour √©viter l'√©num√©ration, la logique d'authentification traitera tous les cas de la m√™me mani√®re
    await db
      .update(users)
      .set({ 
        loginAttempts: sql`${users.loginAttempts} + 1`,
        updatedAt: new Date() 
      })
      .where(eq(users.email, email));
  }

  async resetLoginAttempts(email: string): Promise<void> {
    await db
      .update(users)
      .set({ 
        loginAttempts: 0,
        lockUntil: null,
        updatedAt: new Date() 
      })
      .where(eq(users.email, email));
  }

  async lockAccount(email: string, lockDurationMinutes: number): Promise<void> {
    const lockUntil = new Date();
    lockUntil.setMinutes(lockUntil.getMinutes() + lockDurationMinutes);

    await db
      .update(users)
      .set({ 
        lockUntil: lockUntil,
        updatedAt: new Date() 
      })
      .where(eq(users.email, email));
  }

  async isAccountLocked(email: string): Promise<boolean> {
    const user = await this.getUserByEmail(email);
    if (!user || !user.lockUntil) return false;

    const now = new Date();
    const lockUntil = new Date(user.lockUntil);

    if (now < lockUntil) {
      return true;
    } else {
      // D√©verrouiller automatiquement si la p√©riode de verrouillage est expir√©e
      await this.resetLoginAttempts(email);
      return false;
    }
  }

  async checkUserForLogin(email: string): Promise<{ user: User | undefined; isLocked: boolean }> {
    const user = await this.getUserByEmail(email);
    let isLocked = false;

    if (user && user.lockUntil) {
      const now = new Date();
      const lockUntil = new Date(user.lockUntil);

      if (now < lockUntil) {
        isLocked = true;
      } else {
        // D√©verrouiller automatiquement si la p√©riode de verrouillage est expir√©e
        await this.resetLoginAttempts(email);
      }
    }

    return { user, isLocked };
  }




