client/src/components/product-combobox.t.content:
import { useState } from "react";
import { Check, ChevronsUpDown } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { type Product } from "@shared/schema";

interface ProductComboboxProps {
  products: Product[];
  value?: number;
  onChange: (value: number | undefined) => void;
  placeholder?: string;
  disabled?: boolean;
}

export function ProductCombobox({ 
  products, 
  value, 
  onChange, 
  placeholder = "S√©lectionner un produit...",
  disabled = false 
}: ProductComboboxProps) {
  const [open, setOpen] = useState(false);
  const [searchValue, setSearchValue] = useState("");

  const selectedProduct = products.find((product) => product.id === value);

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchValue.toLowerCase()) ||
    product.id.toString().includes(searchValue)
  );

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className="w-full justify-between"
          disabled={disabled}
        >
          {selectedProduct ? selectedProduct.name : placeholder}
          <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-full p-0">
        <Command>
          <CommandInput 
            placeholder="Rechercher un produit..." 
            value={searchValue}
            onValueChange={setSearchValue}
          />
          <CommandList>
            <CommandEmpty>Aucun produit trouv√©.</CommandEmpty>
            <CommandGroup>
              {filteredProducts.map((product) => (
                <CommandItem
                  key={product.id}
                  value={product.id.toString()}
                  onSelect={(currentValue) => {
                    const productId = parseInt(currentValue);
                    onChange(productId === value ? undefined : productId);
                    setOpen(false);
                    setSearchValue("");
                  }}
                >
                  <Check
                    className={cn(
                      "mr-2 h-4 w-4",
                      value === product.id ? "opacity-100" : "opacity-0"
                    )}
                  />
                  <div className="flex flex-col">
                    <span>{product.name}</span>
                    <span className="text-sm text-muted-foreground">
                      {Number(product.priceHT).toLocaleString('fr-FR')} XOF HT
                      {product.stock !== undefined && ` ‚Ä¢ Stock: ${product.stock}`}
                    </span>
                  </div>
                </CommandItem>
              ))}
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

client/src/components/product-search.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery } from "@tanstack/react-query";
import { Check, ChevronsUpDown, Plus, Package } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { type Product } from "@shared/schema";

interface ProductSearchProps {
  value?: number;
  onChange: (value: number | undefined) => void;
  placeholder?: string;
  disabled?: boolean;
  onCreateNew?: (name: string) => void;
  onProductSelect?: (product: Product) => void;
}

export function ProductSearch({ 
  value, 
  onChange, 
  placeholder = "Rechercher un produit...",
  disabled = false,
  onCreateNew,
  onProductSelect
}: ProductSearchProps) {
  const [open, setOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [debouncedQuery, setDebouncedQuery] = useState("");
  const timeoutRef = useRef<NodeJS.Timeout>();

  // Debounce search query
  useEffect(() => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }

    timeoutRef.current = setTimeout(() => {
      setDebouncedQuery(searchQuery);
    }, 300);

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [searchQuery]);

  // Fetch products with search
  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products", debouncedQuery ? { search: debouncedQuery } : {}],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (debouncedQuery) {
        params.append('search', debouncedQuery);
      }
      const response = await fetch(`/api/products?${params.toString()}`, {
        credentials: 'include'
      });
      if (!response.ok) throw new Error('Failed to fetch products');
      return response.json();
    },
    retry: false,
  });

  // Fetch selected product separately to ensure it's always available for display
  const { data: selectedProduct } = useQuery<Product>({
    queryKey: ["/api/products", value],
    queryFn: async () => {
      const response = await fetch(`/api/products/${value}`, {
        credentials: 'include'
      });
      if (!response.ok) throw new Error('Failed to fetch product');
      return response.json();
    },
    enabled: !!value,
    retry: false,
  });

  const formatCurrency = (amount: string | number) => {
    const numAmount = typeof amount === 'string' ? parseFloat(amount) : amount;
    return `${numAmount.toLocaleString('fr-FR')} F CFA`;
  };

  const handleSelect = (productId: string) => {
    console.log("Product selection attempt:", productId);

    if (productId === "create-new" && onCreateNew && searchQuery) {
      onCreateNew(searchQuery);
      setOpen(false);
      setSearchQuery("");
      return;
    }

    const id = parseInt(productId);
    console.log("Parsed product ID:", id);

    if (!isNaN(id)) {
      const product = products.find(p => p.id === id);

      onChange(id);
      if (product && onProductSelect) {
        onProductSelect(product);
      }

      setOpen(false);
      setSearchQuery("");
    }
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className="w-full justify-between"
          disabled={disabled}
        >
          {selectedProduct ? (
            <div className="flex items-center space-x-2">
              <Package className="w-4 h-4" />
              <span>{selectedProduct.name}</span>
              <span className="text-sm text-muted-foreground">
                ‚Ä¢ {formatCurrency(selectedProduct.priceHT)} HT
                {selectedProduct.stock !== undefined && ` ‚Ä¢ Stock: ${selectedProduct.stock}`}
              </span>
            </div>
          ) : (
            <span className="text-muted-foreground">{placeholder}</span>
          )}
          <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-[400px] p-0" align="start">
        <Command shouldFilter={false} value="" onValueChange={() => {}}>
          <CommandInput 
            placeholder="Tapez pour rechercher..." 
            value={searchQuery}
            onValueChange={setSearchQuery}
          />
          <CommandList className="max-h-[200px] overflow-y-auto">
            {isLoading ? (
              <CommandEmpty>Recherche en cours...</CommandEmpty>
            ) : products.length === 0 ? (
              <CommandEmpty>
                {searchQuery ? (
                  <div className="text-center py-4">
                    <p className="text-sm text-muted-foreground mb-2">
                      Aucun produit trouv√© pour "{searchQuery}"
                    </p>
                    {onCreateNew && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleSelect("create-new")}
                        className="text-primary"
                      >
                        <Plus className="w-4 h-4 mr-2" />
                        Cr√©er "{searchQuery}"
                      </Button>
                    )}
                  </div>
                ) : (
                  "Aucun produit trouv√©"
                )}
              </CommandEmpty>
            ) : (
              <CommandGroup>
                {products.map((product) => (
                  <CommandItem
                    key={product.id}
                    value={product.id.toString()}
                    onSelect={() => handleSelect(product.id.toString())}
                    onClick={() => handleSelect(product.id.toString())}
                    className="cursor-pointer hover:bg-gray-100"
                  >
                    <Check
                      className={cn(
                        "mr-2 h-4 w-4",
                        value === product.id ? "opacity-100" : "opacity-0"
                      )}
                    />
                    <div className="flex flex-col w-full">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-2">
                          <Package className="w-4 h-4" />
                          <span className="font-medium">{product.name}</span>
                        </div>
                        <span className="text-sm font-medium">
                          {formatCurrency(product.priceHT)} HT
                        </span>
                      </div>
                      <div className="text-sm text-muted-foreground">
                        {product.description && `${product.description} ‚Ä¢ `}
                        {product.stock !== undefined && `Stock: ${product.stock}`}
                      </div>
                    </div>
                  </CommandItem>
                ))}
                {searchQuery && onCreateNew && !products.some(p => 
                  p.name.toLowerCase().includes(searchQuery.toLowerCase())
                ) && (
                  <CommandItem
                    value="create-new"
                    onSelect={() => handleSelect("create-new")}
                    className="text-primary cursor-pointer"
                  >
                    <Plus className="mr-2 h-4 w-4" />
                    Cr√©er "{searchQuery}"
                  </CommandItem>
                )}
              </CommandGroup>
            )}
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

