import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  BarChart3, 
  Users, 
  Package, 
  Tags, 
  FileText, 
  TrendingUp, 
  Settings, 
  Download,
  LogOut,
  X,
  Shield,
  Calculator,
  Bell,
  BookOpen
} from "lucide-react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { useSettings } from "@/hooks/useSettings";
import { useTranslation } from "@/lib/i18n";
import { useQuery } from "@tanstack/react-query";
import type { SelectBusinessAlert } from "@shared/schema";



export default function Sidebar() {
  const [location] = useLocation();
  const { user } = useAuth();
  const { settings } = useSettings();
  const { t } = useTranslation(settings?.language);

  // Get unread alerts count
  const { data: alerts = [] } = useQuery<SelectBusinessAlert[]>({
    queryKey: ['/api/alerts', true], // unreadOnly = true
    queryFn: async () => {
      const response = await fetch('/api/alerts?unreadOnly=true');
      if (!response.ok) {
        throw new Error('Failed to fetch alerts');
      }
      return response.json();
    },
    refetchInterval: 30000, // Refresh every 30 seconds
    enabled: !!user, // Only fetch if user is authenticated
  });

  const unreadAlertsCount = alerts.length;

  const navigation = [
    { name: t('dashboard'), href: "/", icon: BarChart3 },
    { name: t('clients'), href: "/clients", icon: Users },
    { name: t('products'), href: "/products", icon: Package },
    { name: t('categories'), href: "/categories", icon: Tags },
    { name: t('invoices'), href: "/invoices", icon: FileText },
    { name: t('sales'), href: "/sales", icon: TrendingUp },
    { name: t('accounting'), href: "/accounting", icon: Calculator },
    { name: t('alerts'), href: "/alerts", icon: Bell },
  ];

  const secondaryNavigation = [
    { name: t('settings'), href: "/settings", icon: Settings },
    { name: t('export'), href: "/export", icon: Download },
    { name: "Guide utilisateur", href: "/user-guide", icon: BookOpen },
  ];

  // Add admin navigation for Fatimata
  const adminNavigation = [
    { name: "Admin Licences", href: "/admin", icon: Shield },
  ];

  return (
    <div className="w-64 sidebar-glass flex flex-col">
      {/* Logo and Title */}
      <div className="p-6 border-b border-white/20 dark:border-white/10">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
            <BarChart3 className="text-white text-lg" />
          </div>
          <div>
            <h1 className="text-xl font-bold text-gray-900">YGestion</h1>
            <p className="text-sm text-gray-500">v1.0.0</p>
          </div>
        </div>
      </div>

      {/* Navigation Menu */}
      <nav className="flex-1 px-4 py-6 space-y-2">
        {navigation.map((item) => {
          const Icon = item.icon;
          const isActive = location === item.href;
          const isAlertsItem = item.href === "/alerts";

          return (
            <Link key={item.name} href={item.href}>
              <Button
                variant="ghost"
                className={cn(
                  "w-full justify-start relative transition-all duration-200 ease-in-out transform hover:scale-[1.02] hover:shadow-sm",
                  isActive
                    ? "bg-primary text-white hover:bg-primary/90 shadow-sm"
                    : "text-gray-700 dark:text-gray-300 hover:bg-white/30 dark:hover:bg-white/20 hover:text-gray-900 dark:hover:text-white"
                )}
              >
                <Icon className="mr-3 h-4 w-4" />
                {item.name}
                {isAlertsItem && unreadAlertsCount > 0 && (
                  <Badge 
                    variant="destructive" 
                    className="ml-auto text-xs h-5 px-1.5 min-w-[20px] flex items-center justify-center"
                  >
                    {unreadAlertsCount > 99 ? '99+' : unreadAlertsCount}
                  </Badge>
                )}
              </Button>
            </Link>
          );
        })}

        <div className="border-t border-white/20 dark:border-white/10 pt-4 mt-4 space-y-2">
          {secondaryNavigation.map((item) => {
            const Icon = item.icon;
            const isActive = location === item.href;

            return (
              <Link key={item.name} href={item.href}>
                <Button
                  variant="ghost"
                  className={cn(
                    "w-full justify-start transition-all duration-200 ease-in-out transform hover:scale-[1.02] hover:shadow-sm",
                    isActive
                      ? "bg-primary text-white hover:bg-primary/90 shadow-sm"
                      : "text-gray-700 dark:text-gray-300 hover:bg-white/30 dark:hover:bg-white/20 hover:text-gray-900 dark:hover:text-white"
                  )}
                >
                  <Icon className="mr-3 h-4 w-4" />
                  {item.name}
                </Button>
              </Link>
            );
          })}

          {/* Admin navigation for Youssouphafils */}
          {(user?.firstName?.toLowerCase() === "youssouphafils" || user?.email?.toLowerCase().includes("youssouphafils")) && (
            <div className="border-t border-white/20 dark:border-white/10 pt-2 mt-2">
              {adminNavigation.map((item) => {
                const Icon = item.icon;
                const isActive = location === item.href;

                return (
                  <Link key={item.name} href={item.href}>
                    <Button
                      variant="ghost"
                      className={cn(
                        "w-full justify-start transition-all duration-200 ease-in-out transform hover:scale-[1.02] hover:shadow-sm",
                        isActive
                          ? "bg-purple-600 text-white hover:bg-purple-700 shadow-sm"
                          : "text-purple-700 hover:bg-purple-100 hover:text-purple-800"
                      )}
                    >
                      <Icon className="mr-3 h-4 w-4" />
                      {item.name}
                    </Button>
                  </Link>
                );
              })}
            </div>
          )}
        </div>
      </nav>

      {/* User Profile */}
      <div className="p-4 border-t border-white/20 dark:border-white/10">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center overflow-hidden">
            {user?.profileImageUrl ? (
              <img 
                src={user.profileImageUrl} 
                alt="Profile" 
                className="w-full h-full object-cover"
              />
            ) : (
              <Users className="w-4 h-4 text-gray-600" />
            )}
          </div>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-gray-900 truncate">
              {user?.firstName && user?.lastName 
                ? `${user.firstName} ${user.lastName}`
                : user?.email || "Utilisateur"
              }
            </p>
            <p className="text-xs text-gray-500">Administrateur</p>
          </div>
          <div className="flex space-x-1">
            <Button
              variant="ghost"
              size="sm"
              className="text-gray-400 hover:text-red-600 hover:bg-red-50 transition-all duration-200 ease-in-out transform hover:scale-105"
              onClick={() => {
                if (confirm(settings?.language === 'en' 
                  ? 'Are you sure you want to close the application?' 
                  : '√ätes-vous s√ªr de vouloir fermer l\'application ?'
                )) {
                  window.close();
                }
              }}
              title={settings?.language === 'en' ? 'Close Application' : 'Fermer l\'application'}
            >
              <X className="w-4 h-4" />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              className="text-gray-400 hover:text-gray-600 hover:bg-gray-50 transition-all duration-200 ease-in-out transform hover:scale-105"
              onClick={async () => {
                try {
                  await fetch('/api/logout', { method: 'POST' });
                  window.location.href = '/';
                } catch (error) {
                  console.error('Erreur lors de la d√©connexion:', error);
                }
              }}
              title={settings?.language === 'en' ? 'Logout' : 'Se d√©connecter'}
            >
              <LogOut className="w-4 h-4" />
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/simple-client-sele.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Check, ChevronsUpDown, User } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Input } from "@/components/ui/input";
import { type Client } from "@shared/schema";

interface SimpleClientSelectProps {
  value?: number;
  onChange: (value: number | undefined) => void;
  placeholder?: string;
  disabled?: boolean;
}

export function SimpleClientSelect({ 
  value, 
  onChange, 
  placeholder = "Rechercher ou cr√©er un client...",
  disabled = false,
}: SimpleClientSelectProps) {
  const [open, setOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch all clients
  const { data: clients = [] } = useQuery<Client[]>({
    queryKey: ["/api/clients"],
    retry: false,
  });

  // Get selected client
  const selectedClient = clients.find(c => c.id === value);

  // Filter clients based on search
  const filteredClients = clients.filter(client =>
    client.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    (client.email && client.email.toLowerCase().includes(searchQuery.toLowerCase())) ||
    (client.company && client.company.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  const handleSelect = (clientId: number) => {
    onChange(clientId);
    setOpen(false);
    setSearchQuery("");
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className="w-full justify-between"
          disabled={disabled}
        >
          {selectedClient ? (
            <div className="flex items-center space-x-2">
              <User className="w-4 h-4" />
              <span>{selectedClient.name}</span>
              {selectedClient.company && (
                <span className="text-sm text-muted-foreground">- {selectedClient.company}</span>
              )}
            </div>
          ) : (
            <span className="text-muted-foreground">{placeholder}</span>
          )}
          <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-[400px] p-0" align="start">
        <div className="p-3 border-b">
          <Input
            placeholder="Tapez pour rechercher..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="h-9"
          />
        </div>
        <div className="max-h-[200px] overflow-y-auto">
          {filteredClients.length === 0 ? (
            <div className="p-4 text-center text-sm text-muted-foreground">
              {searchQuery ? `Aucun client trouv√© pour "${searchQuery}"` : "Aucun client trouv√©"}
            </div>
          ) : (
            <div className="p-1">
              {filteredClients.map((client) => (
                <div
                  key={client.id}
                  onClick={() => handleSelect(client.id)}
                  className={cn(
                    "flex items-center space-x-2 rounded-sm px-2 py-2 cursor-pointer hover:bg-accent hover:text-accent-foreground",
                    value === client.id && "bg-accent text-accent-foreground"
                  )}
                >
                  <Check
                    className={cn(
                      "mr-2 h-4 w-4",
                      value === client.id ? "opacity-100" : "opacity-0"
                    )}
                  />
                  <div className="flex flex-col flex-1">
                    <div className="flex items-center space-x-2">
                      <User className="w-4 h-4" />
                      <span className="font-medium">{client.name}</span>
                    </div>
                    <div className="text-sm text-muted-foreground">
                      {client.company && `${client.company} ‚Ä¢ `}
                      {client.email}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </PopoverContent>
    </Popover>
  );
}

client/src/components/simple-product-sel.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Check, ChevronsUpDown, Package } from "lucide-react";
import { cn } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { Input } from "@/components/ui/input";
import { type Product } from "@shared/schema";

interface SimpleProductSelectProps {
  value?: number;
  onChange: (value: number | undefined) => void;
  onProductSelect?: (product: Product) => void;
  placeholder?: string;
  disabled?: boolean;
}

export function SimpleProductSelectV2({ 
  value, 
  onChange, 
  onProductSelect,
  placeholder = "Rechercher un produit...",
  disabled = false,
}: SimpleProductSelectProps) {
  const [open, setOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch all products
  const { data: products = [] } = useQuery<Product[]>({
    queryKey: ["/api/products"],
    retry: false,
  });

  // Get selected product
  const selectedProduct = products.find(p => p.id === value);

  // Filter products based on search
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    (product.description && product.description.toLowerCase().includes(searchQuery.toLowerCase()))
  );

  const formatCurrency = (amount: string | number) => {
    const numAmount = typeof amount === 'string' ? parseFloat(amount) : amount;
    return `${numAmount.toLocaleString('fr-FR')} F CFA`;
  };

  const handleSelect = (productId: number) => {
    const product = products.find(p => p.id === productId);

    onChange(productId);
    if (product && onProductSelect) {
      onProductSelect(product);
    }

    setOpen(false);
    setSearchQuery("");
  };

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className="w-full justify-between"
          disabled={disabled}
        >
          {selectedProduct ? (
            <div className="flex items-center space-x-2">
              <Package className="w-4 h-4" />
              <span>{selectedProduct.name}</span>
              <span className="text-sm text-muted-foreground">
                ‚Ä¢ {formatCurrency(selectedProduct.priceHT)} HT
                {selectedProduct.stock !== undefined && ` ‚Ä¢ Stock: ${selectedProduct.stock}`}
              </span>
            </div>
          ) : (
            <span className="text-muted-foreground">{placeholder}</span>
          )}
          <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-[400px] p-0" align="start">
        <div className="p-3 border-b">
          <Input
            placeholder="Tapez pour rechercher..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="h-9"
          />
        </div>
        <div className="max-h-[200px] overflow-y-auto">
          {filteredProducts.length === 0 ? (
            <div className="p-4 text-center text-sm text-muted-foreground">
              {searchQuery ? `Aucun produit trouv√© pour "${searchQuery}"` : "Aucun produit trouv√©"}
            </div>
          ) : (
            <div className="p-1">
              {filteredProducts.map((product) => (
                <div
                  key={product.id}
                  onClick={() => handleSelect(product.id)}
                  className={cn(
                    "flex items-center space-x-2 rounded-sm px-2 py-2 cursor-pointer hover:bg-accent hover:text-accent-foreground",
                    value === product.id && "bg-accent text-accent-foreground"
                  )}
                >
                  <Check
                    className={cn(
                      "mr-2 h-4 w-4",
                      value === product.id ? "opacity-100" : "opacity-0"
                    )}
                  />
                  <div className="flex flex-col w-full">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center space-x-2">
                        <Package className="w-4 h-4" />
                        <span className="font-medium">{product.name}</span>
                      </div>
                      <span className="text-sm font-medium">
                        {formatCurrency(product.priceHT)} HT
                      </span>
                    </div>
                    <div className="text-sm text-muted-foreground">
                      {product.description && `${product.description} ‚Ä¢ `}
                      {product.stock !== undefined && `Stock: ${product.stock}`}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </PopoverContent>
    </Popover>
  );
}

import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { type Product } from "@shared/schema";

interface SimpleProductSelectProps {
  products: Product[];
  value?: number;
  onChange: (value: number | undefined) => void;
  placeholder?: string;
  disabled?: boolean;
}

export function SimpleProductSelect({ 
  products, 
  value, 
  onChange, 
  placeholder = "S√©lectionner un produit...",
  disabled = false 
}: SimpleProductSelectProps) {
  const selectedProduct = products.find((product) => product.id === value);

  return (
    <Select 
      onValueChange={(selectedValue) => {
        if (selectedValue === "") {
          onChange(undefined);
        } else {
          onChange(parseInt(selectedValue));
        }
      }}
      value={value ? value.toString() : ""}
      disabled={disabled}
    >
      <SelectTrigger className="w-full">
        <SelectValue placeholder={placeholder} />
      </SelectTrigger>
      <SelectContent>
        {products.map((product) => (
          <SelectItem key={product.id} value={product.id.toString()}>
            <div className="flex flex-col">
              <span>{product.name}</span>
              <span className="text-sm text-muted-foreground">
                {Number(product.priceHT).toLocaleString('fr-FR')} XOF HT
                {product.stock !== undefined && ` ‚Ä¢ Stock: ${product.stock}`}
              </span>
            </div>
          </SelectItem>
        ))}
      </SelectContent>
