import { ExpenseManager } from "../components/accounting/expense-manager";
import { ImprestManager } from "../components/accounting/imprest-manager";
import { ReportsManager } from "../components/accounting/reports-manager";
import { RevenueManager } from "../components/accounting/revenue-manager";
import { useTranslation, formatPrice } from "@/lib/i18n";
import { useSettings } from "@/hooks/useSettings";

import { EXPENSE_STATUS, PAYMENT_METHODS, IMPREST_STATUS } from "@shared/schema";

interface AccountingStats {
  totalExpenses: number;
  pendingExpenses: number;
  approvedExpenses: number;
  totalImprestFunds: number;
  activeImprestFunds: number;
  totalRevenues: number;
  monthlyRevenues: number;
  recentRevenues: number;
  netResult: number; // R√©sultat net (Revenus - D√©penses)
  monthlyExpensesByCategory: { category: string; amount: number; allocatedAmount: number }[];
  recentExpenses: any[];
}

export default function AccountingPage() {
  const [startDate, setStartDate] = useState<string>("");
  const [endDate, setEndDate] = useState<string>("");
  const [showDateFilter, setShowDateFilter] = useState(false);
  const { settings } = useSettings();
  const { t } = useTranslation(settings?.language);

  const { data: stats, isLoading: statsLoading } = useQuery<AccountingStats>({
    queryKey: ["/api/accounting/stats", startDate, endDate],
    queryFn: () => {
      const params = new URLSearchParams();
      if (startDate && endDate) {
        params.append('startDate', startDate);
        params.append('endDate', endDate);
      }
      return fetch(`/api/accounting/stats?${params.toString()}`, {
        credentials: 'include'
      }).then(res => res.json());
    },
  });

  const { data: expenses } = useQuery({
    queryKey: ["/api/accounting/expenses"],
  });

  const { data: imprestFunds } = useQuery({
    queryKey: ["/api/accounting/imprest-funds"],
  });

  if (statsLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">{t('accounting')}</h1>
          <p className="text-muted-foreground">
            {t('manageFinances')}
          </p>
        </div>
        <Button 
          variant="outline" 
          onClick={() => setShowDateFilter(!showDateFilter)}
          data-testid="button-toggle-stats-date-filter"
        >
          <Filter className="mr-2 h-4 w-4" />
          {t('filterByPeriod')}
        </Button>
      </div>

      {/* Filtre par p√©riode pour les statistiques */}
      {showDateFilter && (
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <Calendar className="h-4 w-4" />
                <Label htmlFor="stats-start-date">{t('from')} :</Label>
                <Input
                  id="stats-start-date"
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-auto"
                  data-testid="input-stats-start-date"
                />
              </div>
              <div className="flex items-center gap-2">
                <Label htmlFor="stats-end-date">{t('to')} :</Label>
                <Input
                  id="stats-end-date"
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-auto"
                  data-testid="input-stats-end-date"
                />
              </div>
              <Button 
                variant="outline" 
                onClick={() => {
                  setStartDate("");
                  setEndDate("");
                }}
                data-testid="button-clear-stats-filter"
              >
                {t('clear')}
              </Button>
            </div>
            {startDate && endDate && (
              <p className="text-sm text-muted-foreground mt-2">
                {t('displaying')} {new Date(startDate).toLocaleDateString(settings?.language === 'en' ? 'en-US' : 'fr-FR')} {settings?.language === 'en' ? 'to' : 'au'} {new Date(endDate).toLocaleDateString(settings?.language === 'en' ? 'en-US' : 'fr-FR')}
              </p>
            )}
          </CardContent>
        </Card>
      )}

      {/* Statistics Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">{t('totalRevenues')}</CardTitle>
            <TrendingUp className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-xl font-bold break-words">
              {formatPrice(stats?.totalRevenues || 0, settings?.currency)}
            </div>
            <p className="text-xs text-muted-foreground">
              {stats?.recentRevenues || 0} {t('recentRevenues').toLowerCase()}
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">{t('totalExpenses')}</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-xl font-bold break-words">
              {formatPrice(stats?.totalExpenses || 0, settings?.currency)}
            </div>
            <p className="text-xs text-muted-foreground">
              {stats?.recentExpenses?.length || 0} {settings?.language === 'en' ? 'recent expenses' : 'd√©penses r√©centes'}
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">{t('awaiting')}</CardTitle>
            <TrendingUp className="h-4 w-4 text-orange-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats?.pendingExpenses || 0}</div>
            <p className="text-xs text-muted-foreground">
              {t('expensesToApprove')}
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">{t('approved')}</CardTitle>
            <TrendingDown className="h-4 w-4 text-green-500" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats?.approvedExpenses || 0}</div>
            <p className="text-xs text-muted-foreground">
              {t('validatedExpenses')}
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">{t('imprestFunds')}</CardTitle>
            <Wallet className="h-4 w-4 text-blue-500" />
          </CardHeader>
          <CardContent>
            <div className="text-xl font-bold break-words">
              {formatPrice(stats?.totalImprestFunds || 0, settings?.currency)}
            </div>
            <p className="text-xs text-muted-foreground">
              {stats?.activeImprestFunds || 0} {t('activeFunds')}
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">
              {t('netResult')} {startDate && endDate && `(${t('selectedPeriod')})`}
            </CardTitle>
            {(stats?.netResult || 0) >= 0 ? (
              <TrendingUp className="h-4 w-4 text-green-600" />
            ) : (
              <TrendingDown className="h-4 w-4 text-red-600" />
            )}
          </CardHeader>
          <CardContent>
            <div className={`text-xl font-bold break-words ${(stats?.netResult || 0) >= 0 ? 'text-green-600' : 'text-red-600'}`}>
              {formatPrice(stats?.netResult || 0, settings?.currency)}
            </div>
            <p className="text-xs text-muted-foreground">
              {(stats?.netResult || 0) >= 0 ? (settings?.language === 'en' ? 'Profit' : 'B√©n√©fice') : (settings?.language === 'en' ? 'Loss' : 'Perte')} ({t('revenuesMinusExpenses')})
              {startDate && endDate && (
                <><br />{settings?.language === 'en' ? 'Period' : 'P√©riode'} : {new Date(startDate).toLocaleDateString(settings?.language === 'en' ? 'en-US' : 'fr-FR')} {settings?.language === 'en' ? 'to' : 'au'} {new Date(endDate).toLocaleDateString(settings?.language === 'en' ? 'en-US' : 'fr-FR')}</>
              )}
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Recent Activity */}
      {stats?.recentExpenses && stats.recentExpenses.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>{settings?.language === 'en' ? 'Recent Expenses' : 'D√©penses r√©centes'}</CardTitle>
            <CardDescription>
              {settings?.language === 'en' ? 'Overview of your latest transactions' : 'Aper√ßu de vos derni√®res transactions'}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {stats.recentExpenses.slice(0, 3).map((expense: any) => {
                const status = EXPENSE_STATUS.find(s => s.value === expense.status);
                const paymentMethod = PAYMENT_METHODS.find(p => p.value === expense.paymentMethod);

                return (
                  <div key={expense.id} className="flex items-center justify-between space-x-4">
                    <div className="flex items-center space-x-4">
                      <div className="w-2 h-2 rounded-full bg-primary"></div>
                      <div>
                        <p className="text-sm font-medium">{expense.description}</p>
                        <p className="text-xs text-muted-foreground">
                          {expense.category?.name} ‚Ä¢ {paymentMethod?.label}
                        </p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <span className="text-sm font-medium">
                        {formatPrice(parseFloat(expense.amount), settings?.currency)}
                      </span>
                      <Badge variant="secondary" className={status?.color}>
                        {status?.icon} {status?.label}
                      </Badge>
                    </div>
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Category Breakdown */}
      {stats?.monthlyExpensesByCategory && stats.monthlyExpensesByCategory.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>{settings?.language === 'en' ? 'Expense Overview by Category' : 'Aper√ßu des d√©penses par cat√©gorie'}</CardTitle>
            <CardDescription>
              {settings?.language === 'en' ? 'Breakdown of current month expenses' : 'R√©partition des d√©penses du mois en cours'}
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {stats.monthlyExpensesByCategory.map((item: any, index: number) => {
                const percentage = item.allocatedAmount > 0 ? (item.amount / item.allocatedAmount) * 100 : 0;

                return (
                  <div key={index} className="space-y-2">
                    <div className="flex justify-between text-sm">
                      <span className="font-medium">{item.category}</span>
                      <span className="text-muted-foreground">
                        {formatPrice(item.amount, settings?.currency)} / {formatPrice(item.allocatedAmount, settings?.currency)} ({percentage.toFixed(1)}%)
                      </span>
                    </div>
                    <div className="w-full bg-muted rounded-full h-2">
                      <div 
                        className="bg-primary h-2 rounded-full transition-all" 
                        style={{ width: `${percentage}%` }}
                      />
                    </div>
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Main Tabs */}
      <Tabs defaultValue="expenses" className="space-y-6">
        <div className="flex flex-col sm:flex-row gap-4 items-start">
          <TabsList className="flex flex-col sm:flex-row w-full sm:w-auto h-auto p-1 bg-gray-100 dark:bg-gray-800 rounded-lg shadow-sm">
            <TabsTrigger 
              value="expenses" 
              className="flex items-center justify-start gap-3 w-full sm:w-auto px-6 py-3 text-sm font-medium rounded-md transition-all hover:bg-red-50 dark:hover:bg-red-900/20 data-[state=active]:bg-red-500 data-[state=active]:text-white data-[state=active]:shadow-md hover:shadow-sm"
            >
              <CreditCard className="h-5 w-5" />
              <span className="whitespace-nowrap">{t('expenses')}</span>
            </TabsTrigger>
            <TabsTrigger 
              value="imprest" 
              className="flex items-center justify-start gap-3 w-full sm:w-auto px-6 py-3 text-sm font-medium rounded-md transition-all hover:bg-blue-50 dark:hover:bg-blue-900/20 data-[state=active]:bg-blue-500 data-[state=active]:text-white data-[state=active]:shadow-md hover:shadow-sm"
            >
              <Wallet className="h-5 w-5" />
              <span className="whitespace-nowrap">{t('imprestFunds')}</span>
            </TabsTrigger>

            <TabsTrigger 
              value="revenues" 
              className="flex items-center justify-start gap-3 w-full sm:w-auto px-6 py-3 text-sm font-medium rounded-md transition-all hover:bg-green-50 dark:hover:bg-green-900/20 data-[state=active]:bg-green-500 data-[state=active]:text-white data-[state=active]:shadow-md hover:shadow-sm"
            >
              <TrendingUp className="h-5 w-5" />
              <span className="whitespace-nowrap">{t('revenues')}</span>
            </TabsTrigger>
            <TabsTrigger 
              value="reports" 
              className="flex items-center justify-start gap-3 w-full sm:w-auto px-6 py-3 text-sm font-medium rounded-md transition-all hover:bg-purple-50 dark:hover:bg-purple-900/20 data-[state=active]:bg-purple-500 data-[state=active]:text-white data-[state=active]:shadow-md hover:shadow-sm"
            >
              <FileText className="h-5 w-5" />
              <span className="whitespace-nowrap">{t('reports')}</span>
            </TabsTrigger>
          </TabsList>
        </div>

        <TabsContent value="expenses">
          <ExpenseManager />
        </TabsContent>

        <TabsContent value="imprest">
          <ImprestManager />
        </TabsContent>

        <TabsContent value="revenues">
          <RevenueManager />
        </TabsContent>

        <TabsContent value="reports">
          <ReportsManager />
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/admin-licenses.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Plus, Shield, Key, AlertCircle, Check, X, Ban } from "lucide-react";
import { format } from "date-fns";
import { fr } from "date-fns/locale";
import type { License } from "@shared/schema";

export default function AdminLicensesPage() {
  const [adminToken, setAdminToken] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [newLicense, setNewLicense] = useState({
    key: "",
    clientName: "",
    createdBy: "Youssouphafils",
  });
  const [createDialogOpen, setCreateDialogOpen] = useState(false);

  const queryClient = useQueryClient();

  // Query to fetch all licenses
  const {
    data: licenses,
    isLoading,
    error,
  } = useQuery({
    queryKey: ["admin-licenses"],
    queryFn: async (): Promise<License[]> => {
      const response = await fetch("/api/admin/licenses", {
        headers: {
          "x-admin-token": adminToken,
        },
      });

      if (!response.ok) {
        throw new Error("Acc√®s refus√© ou erreur serveur");
      }

      return response.json();
    },
    enabled: isAuthenticated,
  });

  // Mutation to create a new license
  const createLicenseMutation = useMutation({
    mutationFn: async (licenseData: typeof newLicense) => {
      const response = await fetch("/api/admin/licenses", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "x-admin-token": adminToken,
        },
        body: JSON.stringify(licenseData),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Erreur lors de la cr√©ation");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["admin-licenses"] });
      setNewLicense({ key: "", clientName: "", createdBy: "Youssouphafils" });
      setCreateDialogOpen(false);
    },
  });

  // Mutation to revoke a license
  const revokeLicenseMutation = useMutation({
    mutationFn: async (key: string) => {
      const response = await fetch(`/api/admin/licenses/${key}/revoke`, {
        method: "PATCH",
        headers: {
          "x-admin-token": adminToken,
        },
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Erreur lors de la r√©vocation");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["admin-licenses"] });
    },
  });

  const handleAuthenticate = () => {
    if (adminToken.trim()) {
      setIsAuthenticated(true);
    }
  };

  const handleCreateLicense = () => {
    if (!newLicense.key.trim()) return;
    createLicenseMutation.mutate(newLicense);
  };

  const generateLicenseKey = () => {
    const year = new Date().getFullYear();
    const random = Math.random().toString(36).substr(2, 3).toUpperCase();
    const sequence = String(Math.floor(Math.random() * 999) + 1).padStart(3, '0');
    return `WENIBAC-${year}-${sequence}-${random}`;
  };

  const getStatusBadge = (license: License) => {
    if (license.revokedAt) {
      return <Badge variant="destructive" className="flex items-center gap-1"><Ban className="w-3 h-3" />R√©voqu√©e</Badge>;
    }
    if (license.activated) {
      return <Badge variant="default" className="flex items-center gap-1 bg-green-500"><Check className="w-3 h-3" />Activ√©e</Badge>;
    }
    return <Badge variant="secondary" className="flex items-center gap-1"><Key className="w-3 h-3" />En attente</Badge>;
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-100 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center p-4">
        <Card className="w-full max-w-md shadow-xl">
          <CardHeader className="text-center pb-4">
            <div className="mx-auto w-12 h-12 bg-purple-100 dark:bg-purple-900 rounded-full flex items-center justify-center mb-4">
              <Shield className="w-6 h-6 text-purple-600 dark:text-purple-400" />
            </div>
            <CardTitle className="text-2xl font-bold">Administration YGestion</CardTitle>
            <CardDescription>
              Acc√®s r√©serv√© √† l'administrateur
            </CardDescription>
          </CardHeader>

          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="adminToken">Token d'administration</Label>
              <Input
                id="adminToken"
                type="password"
                placeholder="Entrez votre token admin"
                value={adminToken}
                onChange={(e) => setAdminToken(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && handleAuthenticate()}
                data-testid="input-admin-token"
              />
            </div>

            <Button 
              onClick={handleAuthenticate}
              className="w-full"
              disabled={!adminToken.trim()}
              data-testid="button-authenticate"
            >
              Se connecter
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-6xl mx-auto space-y-6">
        {/* Header */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-2xl">Administration des Licences</CardTitle>
                <CardDescription>
                  Gestion des cl√©s d'activation YGestion
                </CardDescription>
              </div>
              <Dialog open={createDialogOpen} onOpenChange={setCreateDialogOpen}>
                <DialogTrigger asChild>
                  <Button data-testid="button-new-license">
                    <Plus className="w-4 h-4 mr-2" />
                    Nouvelle licence
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Cr√©er une nouvelle licence</DialogTitle>
                    <DialogDescription>
                      G√©n√©rez une nouvelle cl√© d'activation pour un client
                    </DialogDescription>
                  </DialogHeader>

                  <div className="space-y-4 py-4">
                    <div className="space-y-2">
                      <Label htmlFor="licenseKey">Cl√© d'activation</Label>
                      <div className="flex gap-2">
                        <Input
                          id="licenseKey"
                          value={newLicense.key}
                          onChange={(e) => setNewLicense(prev => ({ ...prev, key: e.target.value }))}
                          className="font-mono"
                          placeholder="WENIBAC-2025-001-ABC"
                          data-testid="input-new-license-key"
                        />
                        <Button
                          type="button"
                          variant="outline"
                          onClick={() => setNewLicense(prev => ({ ...prev, key: generateLicenseKey() }))}
                          data-testid="button-generate-key"
                        >
                          G√©n√©rer
                        </Button>
                      </div>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="clientName">Nom du client</Label>
                      <Input
                        id="clientName"
                        value={newLicense.clientName}
                        onChange={(e) => setNewLicense(prev => ({ ...prev, clientName: e.target.value }))}
                        placeholder="Nom du client"
                        data-testid="input-client-name-new"
                      />
                    </div>
                  </div>

                  {createLicenseMutation.isError && (
                    <Alert variant="destructive">
                      <AlertCircle className="h-4 w-4" />
                      <AlertDescription>
                        {createLicenseMutation.error?.message || "Erreur lors de la cr√©ation"}
                      </AlertDescription>
                    </Alert>
                  )}

                  <DialogFooter>
                    <Button 
                      variant="outline" 
                      onClick={() => setCreateDialogOpen(false)}
                      data-testid="button-cancel"
                    >
                      Annuler
                    </Button>
                    <Button 
                      onClick={handleCreateLicense}
                      disabled={!newLicense.key.trim() || createLicenseMutation.isPending}
                      data-testid="button-create-license"
                    >
                      {createLicenseMutation.isPending ? "Cr√©ation..." : "Cr√©er"}
                    </Button>
                  </DialogFooter>
                </DialogContent>
              </Dialog>
            </div>
          </CardHeader>
        </Card>

        {/* Licenses Table */}
        <Card>
          <CardContent className="p-0">
            {isLoading ? (
              <div className="flex items-center justify-center py-8">
                <div className="text-center">
                  <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4"></div>
                  <p className="text-muted-foreground">Chargement des licences...</p>
                </div>
              </div>
            ) : error ? (
              <div className="p-6">
                <Alert variant="destructive">
                  <AlertCircle className="h-4 w-4" />
                  <AlertDescription>
                    Erreur lors du chargement des licences. V√©rifiez votre token d'administration.
                  </AlertDescription>
                </Alert>
              </div>
            ) : (
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Cl√©</TableHead>
                    <TableHead>Client</TableHead>
                    <TableHead>Statut</TableHead>
                    <TableHead>Activ√©e le</TableHead>
                    <TableHead>Cr√©√©e le</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {licenses?.map((license) => (
                    <TableRow key={license.id}>
                      <TableCell>
                        <code className="bg-muted px-2 py-1 rounded font-mono text-sm">
                          {license.key}
                        </code>
                      </TableCell>
                      <TableCell>{license.clientName || "Non sp√©cifi√©"}</TableCell>
                      <TableCell>{getStatusBadge(license)}</TableCell>
                      <TableCell>
                        {license.activatedAt ? (
                          format(new Date(license.activatedAt), "dd/MM/yyyy √† HH:mm", { locale: fr })
                        ) : (
                          "‚Äî"
                        )}
                      </TableCell>
                      <TableCell>
                        {license.createdAt ? (
                          format(new Date(license.createdAt), "dd/MM/yyyy √† HH:mm", { locale: fr })
                        ) : (
                          "‚Äî"
                        )}
                      </TableCell>
                      <TableCell>
                        {license.activated && !license.revokedAt && (
                          <Button
                            variant="destructive"
                            size="sm"
                            onClick={() => revokeLicenseMutation.mutate(license.key)}
                            disabled={revokeLicenseMutation.isPending}
                            data-testid={`button-revoke-${license.key}`}
                          >
                            <X className="w-4 h-4 mr-1" />
                            R√©voquer
                          </Button>
                        )}
                      </TableCell>
                    </TableRow>
                  ))}
                  {licenses?.length === 0 && (
                    <TableRow>
                      <TableCell colSpan={6} className="text-center py-8 text-muted-foreground">
                        Aucune licence cr√©√©e pour le moment
                      </TableCell>
                    </TableRow>
                  )}
                </TableBody>
              </Table>
            )}
          </CardContent>
        </Card>

        {/* Stats */}
        {licenses && licenses.length > 0 && (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <Card>
              <CardContent className="p-4">
                <div className="text-center">
                  <div className="text-2xl font-bold">{licenses.length}</div>
                  <p className="text-sm text-muted-foreground">Total licences</p>
                </div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600">
                    {licenses.filter(l => l.activated && !l.revokedAt).length}
                  </div>
                  <p className="text-sm text-muted-foreground">Actives</p>
                </div>
              </CardContent>
            </Card>
            <Card>
              <CardContent className="p-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-red-600">
                    {licenses.filter(l => l.revokedAt).length}
                  </div>
                  <p className="text-sm text-muted-foreground">R√©voqu√©es</p>
                </div>
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </div>
  );
}

