import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import Header from "@/components/header";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Download, 
  FileText, 
  Users, 
  Package, 
  Database,
  Calendar,
  CheckCircle,
  AlertCircle
} from "lucide-react";

export default function Export() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [exportStatus, setExportStatus] = useState<{ [key: string]: 'idle' | 'loading' | 'success' | 'error' }>({});

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Non autoris√©",
        description: "Vous √™tes d√©connect√©. Reconnexion...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const handleExport = async (type: 'clients' | 'products' | 'invoices') => {
    setExportStatus(prev => ({ ...prev, [type]: 'loading' }));

    try {
      const response = await fetch(`/api/export/${type}`, {
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      // Create blob and download
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `${type}_${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);

      setExportStatus(prev => ({ ...prev, [type]: 'success' }));
      toast({
        title: "Export r√©ussi",
        description: `Les donn√©es ${type} ont √©t√© export√©es avec succ√®s.`,
      });

      // Reset status after 3 seconds
      setTimeout(() => {
        setExportStatus(prev => ({ ...prev, [type]: 'idle' }));
      }, 3000);

    } catch (error) {
      console.error(`Error exporting ${type}:`, error);
      setExportStatus(prev => ({ ...prev, [type]: 'error' }));
      toast({
        title: "Erreur d'export",
        description: `Impossible d'exporter les donn√©es ${type}.`,
        variant: "destructive",
      });

      // Reset status after 3 seconds
      setTimeout(() => {
        setExportStatus(prev => ({ ...prev, [type]: 'idle' }));
      }, 3000);
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'loading':
        return <div className="w-4 h-4 border-2 border-primary border-t-transparent rounded-full animate-spin" />;
      case 'success':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'error':
        return <AlertCircle className="w-4 h-4 text-red-500" />;
      default:
        return <Download className="w-4 h-4" />;
    }
  };

  const getButtonText = (type: string, status: string) => {
    switch (status) {
      case 'loading':
        return 'Export en cours...';
      case 'success':
        return 'Export√© avec succ√®s';
      case 'error':
        return 'Erreur - R√©essayer';
      default:
        return `Exporter ${type}`;
    }
  };

  const isButtonDisabled = (status: string) => {
    return status === 'loading' || status === 'success';
  };

  if (isLoading) {
    return (
      <div className="flex-1 flex flex-col overflow-hidden">
        <Header 
          title="Export & Sauvegarde" 
          subtitle="Exportez vos donn√©es en format CSV"
        />
        <main className="flex-1 overflow-y-auto p-6">
          <div className="animate-pulse space-y-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="bg-white p-6 rounded-xl shadow-sm border border-gray-200 h-32" />
            ))}
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="flex-1 flex flex-col overflow-hidden">
      <Header 
        title="Export & Sauvegarde" 
        subtitle="Exportez vos donn√©es en format CSV"
      />

      <main className="flex-1 overflow-y-auto p-6">
        <div className="max-w-4xl mx-auto space-y-6">
          {/* Info Card */}
          <Card className="bg-blue-50 border-blue-200">
            <CardContent className="p-6">
              <div className="flex items-start space-x-3">
                <Database className="w-6 h-6 text-blue-600 mt-1" />
                <div>
                  <h3 className="font-semibold text-blue-900 mb-2">√Ä propos des exports</h3>
                  <p className="text-blue-700 text-sm mb-3">
                    Exportez vos donn√©es au format CSV pour les sauvegarder ou les importer dans d'autres applications.
                    Les fichiers incluent toutes les informations disponibles pour chaque type de donn√©es.
                  </p>
                  <ul className="text-blue-700 text-sm space-y-1">
                    <li>‚Ä¢ Les exports incluent toutes vos donn√©es</li>
                    <li>‚Ä¢ Format CSV compatible avec Excel et autres tableurs</li>
                    <li>‚Ä¢ Encodage UTF-8 pour les caract√®res sp√©ciaux</li>
                    <li>‚Ä¢ Donn√©es √† jour au moment de l'export</li>
                  </ul>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Export Options */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {/* Clients Export */}
            <Card>
              <CardHeader>
                <div className="flex items-center space-x-3">
                  <div className="w-12 h-12 bg-purple-50 rounded-lg flex items-center justify-center">
                    <Users className="w-6 h-6 text-purple-600" />
                  </div>
                  <div>
                    <CardTitle className="text-lg">Clients</CardTitle>
                    <p className="text-sm text-gray-500">Export de la base clients</p>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <p className="text-sm text-gray-600 mb-3">
                    Exporte tous vos clients avec leurs informations compl√®tes : nom, email, t√©l√©phone, adresse, entreprise.
                  </p>
                  <Badge variant="outline" className="mb-3">
                    <Calendar className="w-3 h-3 mr-1" />
                    Inclut les dates de cr√©ation
                  </Badge>
                </div>
                <Button 
                  className="w-full"
                  onClick={() => handleExport('clients')}
                  disabled={isButtonDisabled(exportStatus.clients || 'idle')}
                  variant={exportStatus.clients === 'error' ? 'destructive' : 'default'}
                >
                  {getStatusIcon(exportStatus.clients || 'idle')}
                  <span className="ml-2">
                    {getButtonText('Clients', exportStatus.clients || 'idle')}
                  </span>
                </Button>
              </CardContent>
            </Card>

            {/* Products Export */}
            <Card>
              <CardHeader>
                <div className="flex items-center space-x-3">
                  <div className="w-12 h-12 bg-green-50 rounded-lg flex items-center justify-center">
                    <Package className="w-6 h-6 text-green-600" />
                  </div>
                  <div>
                    <CardTitle className="text-lg">Produits</CardTitle>
                    <p className="text-sm text-gray-500">Export du catalogue</p>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <p className="text-sm text-gray-600 mb-3">
                    Exporte tous vos produits avec les d√©tails : nom, description, prix, stock, cat√©gorie.
                  </p>
                  <Badge variant="outline" className="mb-3">
                    <Calendar className="w-3 h-3 mr-1" />
                    Inclut les niveaux de stock
                  </Badge>
                </div>
                <Button 
                  className="w-full"
