import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Pagination, PaginationContent, PaginationItem, PaginationLink, PaginationNext, PaginationPrevious } from "@/components/ui/pagination";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Plus, Edit, Trash2, Check, X, Eye, DollarSign, Printer, Download, Calendar, Filter, FileDown } from "lucide-react";
import { ExpensePDF } from "./expense-pdf";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useTranslation, formatPrice } from "@/lib/i18n";
import { useSettings } from "@/hooks/useSettings";
import { 
  insertExpenseSchema, 
  insertExpenseCategorySchema, 
  EXPENSE_STATUS, 
  PAYMENT_METHODS,
  type NewExpense,
  type NewExpenseCategory 
} from "@shared/schema";

export function ExpenseManager() {
  const { settings } = useSettings();
  const { t } = useTranslation(settings?.language);
  const [selectedExpense, setSelectedExpense] = useState<any>(null);
  const [isExpenseDialogOpen, setIsExpenseDialogOpen] = useState(false);
  const [isCategoryDialogOpen, setIsCategoryDialogOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(3);
  const [startDate, setStartDate] = useState<string>("");
  const [endDate, setEndDate] = useState<string>("");
  const [showDateFilter, setShowDateFilter] = useState(false);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Queries
  const { data: expenses = [], isLoading: expensesLoading } = useQuery<any[]>({
    queryKey: ["/api/accounting/expenses", startDate, endDate],
    queryFn: () => {
      const params = new URLSearchParams();
      if (startDate && endDate) {
        params.append('startDate', startDate);
        params.append('endDate', endDate);
      }
      return fetch(`/api/accounting/expenses?${params.toString()}`, {
        credentials: 'include'
      }).then(res => res.json());
    },
  });

  const { data: categories = [] } = useQuery<any[]>({
    queryKey: ["/api/accounting/expense-categories"],
  });

  const { data: imprestFunds = [] } = useQuery<any[]>({
    queryKey: ["/api/accounting/imprest-funds"],
  });

  const { data: chartOfAccounts = [] } = useQuery<any[]>({
    queryKey: ["/api/accounting/chart-of-accounts"],
  });

  const { data: user } = useQuery<any>({
    queryKey: ["/api/user"],
  });

  // Forms
  const expenseForm = useForm({
    resolver: zodResolver(insertExpenseSchema),
    defaultValues: {
      description: "",
      amount: "",
      paymentMethod: "cash",
      status: "pending",
      expenseDate: new Date().toISOString().split('T')[0],
      categoryId: undefined,
      accountId: undefined,
      notes: "",
      imprestId: undefined,
    },
  });

  const categoryForm = useForm({
    resolver: zodResolver(insertExpenseCategorySchema),
    defaultValues: {
      name: "",
      description: "",
      isMajor: false,
    },
  });

  // Mutations
  const createExpenseMutation = useMutation({
    mutationFn: (data: InsertExpense) => apiRequest("POST", "/api/accounting/expenses", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/expenses"] });
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/imprest-funds"] });
      setIsExpenseDialogOpen(false);
      expenseForm.reset();
      toast({ title: "D√©pense cr√©√©e avec succ√®s" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Erreur", 
        description: error.message || "Erreur lors de la cr√©ation de la d√©pense",
        variant: "destructive" 
      });
    },
  });

  const createCategoryMutation = useMutation({
    mutationFn: (data: InsertExpenseCategory) => apiRequest("POST", "/api/accounting/expense-categories", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/expense-categories"] });
      setIsCategoryDialogOpen(false);
      categoryForm.reset();
      toast({ title: "Cat√©gorie cr√©√©e avec succ√®s" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Erreur", 
        description: error.message || "Erreur lors de la cr√©ation de la cat√©gorie",
        variant: "destructive" 
      });
    },
  });

  const approveExpenseMutation = useMutation({
    mutationFn: (id: number) => apiRequest("PATCH", `/api/accounting/expenses/${id}/approve`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/expenses"] });
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/imprest-funds"] });
      toast({ title: "D√©pense approuv√©e avec succ√®s" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Erreur", 
        description: error.message || "Erreur lors de l'approbation",
        variant: "destructive" 
      });
    },
  });

  const rejectExpenseMutation = useMutation({
    mutationFn: (id: number) => apiRequest("PATCH", `/api/accounting/expenses/${id}/reject`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/expenses"] });
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/stats"] });
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/imprest-funds"] });
      toast({ title: "D√©pense rejet√©e" });
    },
    onError: (error: any) => {
      toast({ 
        title: "Erreur", 
        description: error.message || "Erreur lors du rejet",
        variant: "destructive" 
      });
    },
  });

  const handleCreateExpense = (data: any) => {
    console.log("Creating expense:", data);
    createExpenseMutation.mutate(data);
  };

  const handleCreateCategory = (data: any) => {
    console.log("Creating category:", data);
    createCategoryMutation.mutate(data);
  };

  // Bulk print/download functions
  const handleBulkPrint = () => {
    // Filtrer seulement les d√©penses approuv√©es
    const approvedExpenses = expenses.filter((expense: any) => expense.status === 'approved');

    if (!approvedExpenses || approvedExpenses.length === 0) {
      toast({
        title: "Aucune d√©pense approuv√©e",
        description: "Il n'y a aucune d√©pense approuv√©e √† imprimer",
        variant: "destructive"
      });
      return;
    }

    const printWindow = window.open('', '_blank');
    if (!printWindow) return;

    const totalAmount = approvedExpenses.reduce((sum: number, expense: any) => sum + parseFloat(expense.amount), 0);

    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Liste des d√©penses</title>
          <style>
            body { font-family: Arial, sans-serif; margin: 20px; }
            .header { text-align: center; margin-bottom: 30px; border-bottom: 2px solid #333; padding-bottom: 20px; }
            .expenses-table { width: 100%; border-collapse: collapse; margin-top: 20px; }
            .expenses-table th, .expenses-table td { 
              border: 1px solid #ddd; 
              padding: 12px; 
              text-align: left; 
            }
            .expenses-table th { 
              background-color: #f5f5f5; 
              font-weight: bold; 
            }
            .expenses-table tbody tr:nth-child(even) { 
              background-color: #f9f9f9; 
            }
            .amount { text-align: right; font-weight: bold; }
            .summary { margin-top: 30px; padding-top: 20px; border-top: 2px solid #333; }
            .total { font-size: 1.2em; font-weight: bold; text-align: right; }
            @media print { 
              body { margin: 0; }
              .no-print { display: none; }
            }
          </style>
        </head>
        <body>
          <div class="header">
            <h1>LISTE DES D√âPENSES APPROUV√âES</h1>
            <p>G√©n√©r√© le ${new Date().toLocaleDateString('fr-FR')}</p>
          </div>

          <table class="expenses-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Description</th>
                <th>Montant ({settings?.currency === 'GHS' ? 'GHS' : 'FCFA'})</th>
              </tr>
            </thead>
            <tbody>
              ${approvedExpenses.map((expense: any) => `
                <tr>
                  <td>${new Date(expense.expenseDate).toLocaleDateString('fr-FR')}</td>
                  <td>${expense.description}</td>
                  <td class="amount">${parseFloat(expense.amount).toLocaleString('fr-FR', { useGrouping: true }).replace(/\s/g, ' ')}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>

          <div class="summary">
            <div class="total">
              <p>TOTAL: {formatPrice(totalAmount, settings?.currency)}</p>
            </div>
            <p><em>Nombre de d√©penses approuv√©es: ${approvedExpenses.length}</em></p>
          </div>
        </body>
      </html>
    `);

    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  const handleBulkDownload = async () => {
    // Filtrer seulement les d√©penses approuv√©es
    const approvedExpenses = expenses.filter((expense: any) => expense.status === 'approved');

    if (!approvedExpenses || approvedExpenses.length === 0) {
      toast({
        title: "Aucune d√©pense approuv√©e",
        description: "Il n'y a aucune d√©pense approuv√©e √† t√©l√©charger",
        variant: "destructive"
      });
      return;
    }

    try {
      const { jsPDF } = await import('jspdf');
      const html2canvas = (await import('html2canvas')).default;

      const pdf = new jsPDF('p', 'mm', 'a4');
      const pageWidth = pdf.internal.pageSize.getWidth();
      const pageHeight = pdf.internal.pageSize.getHeight();
      const margin = 20;
      const totalAmount = approvedExpenses.reduce((sum: number, expense: any) => sum + parseFloat(expense.amount), 0);

      // Header
      pdf.setFontSize(18);
      pdf.setFont('helvetica', 'bold');
      pdf.text('RAPPORT DE D√âPENSES APPROUV√âES - AUDIT', pageWidth / 2, 30, { align: 'center' });

      pdf.setFontSize(12);
      pdf.setFont('helvetica', 'normal');
      pdf.text(`G√©n√©r√© le ${new Date().toLocaleDateString('fr-FR')}`, pageWidth / 2, 40, { align: 'center' });

      // Company info
      let yPos = 60;
      pdf.setFontSize(14);
      pdf.setFont('helvetica', 'bold');
      pdf.text('Informations de l\'entreprise', margin, yPos);

      yPos += 10;
      pdf.setFontSize(10);
      pdf.setFont('helvetica', 'normal');
      pdf.text(`Entreprise: ${user?.company || 'N/A'}`, margin, yPos);
      yPos += 7;
      pdf.text(`Responsable: ${user?.firstName} ${user?.lastName}`, margin, yPos);
      yPos += 7;
      pdf.text(`Email: ${user?.email}`, margin, yPos);

      // Expenses list
      yPos += 20;
      pdf.setFontSize(14);
      pdf.setFont('helvetica', 'bold');
      pdf.text(`D√©tail des d√©penses approuv√©es (${approvedExpenses.length} d√©pense(s))`, margin, yPos);

      yPos += 15;
      pdf.setFontSize(10);
      pdf.setFont('helvetica', 'normal');

      for (const expense of approvedExpenses) {
        if (yPos > pageHeight - 40) {
          pdf.addPage();
          yPos = 30;
        }

        pdf.setFont('helvetica', 'bold');
        pdf.text(expense.description, margin, yPos);
        pdf.text(`${formatPrice(parseFloat(expense.amount), settings?.currency)}`, pageWidth - margin, yPos, { align: 'right' });

        yPos += 7;
        pdf.setFont('helvetica', 'normal');
        pdf.text(`R√©f√©rence: ${expense.reference}`, margin + 5, yPos);
        yPos += 5;
        pdf.text(`Cat√©gorie: ${expense.category?.name || 'N/A'}`, margin + 5, yPos);
        yPos += 5;
        pdf.text(`Date: ${new Date(expense.expenseDate).toLocaleDateString('fr-FR')}`, margin + 5, yPos);
        yPos += 5;
        pdf.text(`Statut: ${expense.status === 'approved' ? 'Approuv√©e' : expense.status === 'pending' ? 'En attente' : 'Rejet√©e'}`, margin + 5, yPos);
        yPos += 5;
        pdf.text(`Mode: ${PAYMENT_METHODS.find(p => p.value === expense.paymentMethod)?.label || expense.paymentMethod}`, margin + 5, yPos);

        if (expense.notes) {
          yPos += 5;
          pdf.text(`Notes: ${expense.notes}`, margin + 5, yPos);
        }

        yPos += 10;
      }

      // Summary
      if (yPos > pageHeight - 60) {
        pdf.addPage();
        yPos = 30;
      }

      yPos += 10;
      pdf.setFont('helvetica', 'bold');
      pdf.text('R√©sum√©', margin, yPos);
      yPos += 10;
      pdf.setFontSize(12);
      pdf.text(`Total g√©n√©ral: ${formatPrice(totalAmount, settings?.currency)}`, margin, yPos);
      yPos += 8;
      pdf.setFontSize(10);
      pdf.text(`D√©penses approuv√©es: ${expenses.filter((e: any) => e.status === 'approved').length}`, margin, yPos);
      yPos += 6;
      pdf.text(`D√©penses en attente: ${expenses.filter((e: any) => e.status === 'pending').length}`, margin, yPos);
      yPos += 6;
      pdf.text(`D√©penses rejet√©es: ${expenses.filter((e: any) => e.status === 'rejected').length}`, margin, yPos);

      pdf.save(`rapport-depenses-audit-${new Date().toISOString().split('T')[0]}.pdf`);

      toast({
        title: "PDF t√©l√©charg√©",
        description: "Le rapport de d√©penses a √©t√© t√©l√©charg√© avec succ√®s"
      });
    } catch (error) {
      console.error('Erreur lors de la g√©n√©ration du PDF:', error);
      toast({
        title: "Erreur",
        description: "Erreur lors de la g√©n√©ration du PDF",
        variant: "destructive"
      });
    }
  };

  // T√©l√©charger toutes les d√©penses en CSV
  const handleDownloadCSV = () => {
    if (expenses.length === 0) {
      toast({
        title: "Aucune d√©pense",
        description: "Il n'y a aucune d√©pense √† exporter.",
        variant: "destructive"
      });
      return;
    }

    // En-t√™tes CSV
    const headers = [
      'R√©f√©rence',
      'Date',
      'Description', 
      'Cat√©gorie',
      'Mode de paiement',
      `Montant (${settings?.currency === 'GHS' ? 'GHS' : 'FCFA'})`,
      'Statut',
      'Notes'
    ];

    // Convertir les donn√©es en CSV
    const csvData = expenses.map((expense: any) => {
      const paymentMethod = PAYMENT_METHODS.find(p => p.value === expense.paymentMethod);
      const status = EXPENSE_STATUS.find(s => s.value === expense.status);
      return [
        expense.reference || '',
        new Date(expense.expenseDate).toLocaleDateString('fr-FR'),
        expense.description || '',
        expense.category?.name || 'Sans cat√©gorie',
        paymentMethod?.label || expense.paymentMethod,
        parseFloat(expense.amount).toFixed(0).replace(/\B(?=(\d{3})+(?!\d))/g, ' '),
        status?.label || expense.status,
        expense.notes || ''
      ];
    });

    // Cr√©er le contenu CSV avec BOM pour Windows
    const csvContent = [
      headers.join(','),
      ...csvData.map(row => row.map(field => `"${field}"`).join(','))
    ].join('\n');

    // T√©l√©charger le fichier avec BOM UTF-8
    const BOM = '\uFEFF';
    const blob = new Blob([BOM + csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    const url = URL.createObjectURL(blob);
    link.setAttribute('href', url);
    link.setAttribute('download', `depenses_${new Date().toISOString().split('T')[0]}.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    toast({
      title: "Export r√©ussi",
      description: `${expenses.length} d√©penses export√©es en CSV.`
    });
  };

  if (expensesLoading) {
    return (
      <div className="flex items-center justify-center h-32">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold tracking-tight">Gestion des d√©penses</h2>
          <p className="text-muted-foreground">
            Suivez et g√©rez toutes vos d√©penses d'entreprise
          </p>
        </div>
        <div className="flex gap-2">
          <Button 
            variant="outline" 
            onClick={() => setShowDateFilter(!showDateFilter)}
            data-testid="button-toggle-date-filter"
          >
            <Filter className="mr-2 h-4 w-4" />
            Filtrer par p√©riode
          </Button>
          <Button 
            variant="outline" 
            onClick={handleDownloadCSV}
            disabled={expenses.length === 0}
            data-testid="button-download-expenses-csv"
          >
            <FileDown className="mr-2 h-4 w-4" />
            T√©l√©charger CSV
          </Button>
          <Dialog open={isCategoryDialogOpen} onOpenChange={setIsCategoryDialogOpen}>
            <DialogTrigger asChild>
              <Button variant="outline">
                <Plus className="mr-2 h-4 w-4" />
                Cat√©gorie
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Nouvelle cat√©gorie de d√©pense</DialogTitle>
                <DialogDescription>
                  Cr√©ez une nouvelle cat√©gorie pour organiser vos d√©penses
                </DialogDescription>
              </DialogHeader>
              <Form {...categoryForm}>
                <form 
                  onSubmit={(e) => {
                    e.preventDefault();
                    console.log("Form submitted");
                    const values = categoryForm.getValues();
                    console.log("Form values:", values);
                    if (values.name && values.name.trim()) {
                      handleCreateCategory(values);
                    } else {
                      toast({
                        title: "Erreur",
                        description: "Le nom de la cat√©gorie est requis",
                        variant: "destructive"
                      });
                    }
                  }} 
                  className="space-y-4"
                >
                  <FormField
                    control={categoryForm.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Nom de la cat√©gorie</FormLabel>
                        <FormControl>
                          <Input placeholder="Frais de d√©placement" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
