  // Mutations
  const createFundMutation = useMutation({
    mutationFn: (data: InsertImprestFund) => apiRequest("POST", "/api/accounting/imprest-funds", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/accounting/imprest-funds"] });
