                            <div className="flex items-center gap-3">
                              <div className={`p-2 rounded-full ${type?.color}`}>
                                {transaction.type === 'deposit' ? (
                                  <ArrowDown className="h-4 w-4" />
                                ) : (
                                  <ArrowUp className="h-4 w-4" />
                                )}
                              </div>
                              <div>
                                <p className="text-sm font-medium">{transaction.description}</p>
                                <p className="text-xs text-muted-foreground">
                                  {new Date(transaction.createdAt).toLocaleDateString('fr-FR')}
                                </p>
                              </div>
                            </div>
                            <div className="text-right">
                              <p className={`text-sm font-medium ${
                                transaction.type === 'deposit' ? 'text-green-600' : 'text-red-600'
                              }`}>
                                {transaction.type === 'deposit' ? '+' : '-'}
                                {formatPrice(amount, settings?.currency)}
                              </p>
                              <p className="text-xs text-muted-foreground">
                                Solde: {formatPrice(parseFloat(transaction.balanceAfter), settings?.currency)}
                              </p>
                            </div>
                          </div>
                        );
                      })
                    )}
                  </div>
                </div>
              </div>
            ) : (
              <div className="text-center py-8">
                <Eye className="mx-auto h-12 w-12 text-muted-foreground" />
                <h3 className="mt-2 text-sm font-semibold">Aucun fonds s√©lectionn√©</h3>
                <p className="mt-1 text-sm text-muted-foreground">
                  S√©lectionnez un fonds pour voir ses d√©tails.
                </p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

