                  <div className="flex items-center gap-2">
                    <Label htmlFor="revenue-end-date">Au :</Label>
                    <Input
                      id="revenue-end-date"
                      type="date"
                      value={endDate}
                      onChange={(e) => setEndDate(e.target.value)}
                      className="w-auto"
                      data-testid="input-revenue-end-date"
