                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-md">
                <DialogHeader>
                  <DialogTitle>
                    {editingCategory ? 'Modifier la Cat√©gorie' : 'Nouvelle Cat√©gorie'}
                  </DialogTitle>
                </DialogHeader>
                <Form {...categoryForm}>
                  <form onSubmit={(e) => {
                    e.preventDefault();
                    console.log("Revenue category form submitted");
                    const values = categoryForm.getValues();
                    console.log("Form values:", values);
                    console.log("Form errors:", categoryForm.formState.errors);
                    if (values.name) {
                      onCategorySubmit(values);
                    }
                  }} className="space-y-4">
                    <FormField
                      control={categoryForm.control}
                      name="name"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Nom de la cat√©gorie</FormLabel>
                          <FormControl>
                            <Input {...field} data-testid="input-category-name" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={categoryForm.control}
                      name="description"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Description</FormLabel>
                          <FormControl>
                            <Textarea {...field} value={field.value || ''} data-testid="textarea-category-description" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <DialogFooter className="flex space-x-2">
                      <Button
                        type="button"
                        variant="outline"
                        onClick={() => {
                          setIsCategoryDialogOpen(false);
                          categoryForm.reset();
                          setEditingCategory(null);
                        }}
                        data-testid="button-cancel-category"
                      >
                        Annuler
                      </Button>
                      <Button 
                        type="submit" 
                        disabled={categoryMutation.isPending}
                        data-testid="button-save-category"
                        onClick={() => console.log("Revenue category button clicked directly")}
                      >
                        {categoryMutation.isPending ? 'Sauvegarde...' : (editingCategory ? 'Modifier' : 'Cr√©er')}
                      </Button>
                    </DialogFooter>
                  </form>
                </Form>
              </DialogContent>
            </Dialog>
          </div>

          {categoriesLoading ? (
            <div className="text-center py-8">Chargement des cat√©gories...</div>
          ) : categories.length === 0 ? (
            <Card>
              <CardContent className="text-center py-8">
                <Building2 className="w-16 h-16 mx-auto text-gray-400 mb-4" />
                <p className="text-gray-500">Aucune cat√©gorie cr√©√©e</p>
                <p className="text-sm text-gray-400 mt-2">Cr√©ez des cat√©gories pour organiser vos revenus</p>
              </CardContent>
            </Card>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {categories.map((category) => (
                <Card key={category.id}>
                  <CardHeader>
                    <CardTitle className="flex justify-between items-start">
                      <span>{category.name}</span>
                      <div className="flex space-x-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleEditCategory(category)}
                          data-testid={`button-edit-category-${category.id}`}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => deleteCategoryMutation.mutate(category.id)}
                          data-testid={`button-delete-category-${category.id}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </CardTitle>
                    {category.description && (
                      <CardDescription>{category.description}</CardDescription>
                    )}
                  </CardHeader>
                </Card>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Dialog pour les d√©tails du revenu */}
      <Dialog open={isDetailsDialogOpen} onOpenChange={setIsDetailsDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>D√©tails du Revenu</DialogTitle>
          </DialogHeader>
          {selectedRevenue && (
            <div className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label className="text-sm font-medium">R√©f√©rence</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400">{selectedRevenue.reference}</p>
                </div>
                <div>
                  <Label className="text-sm font-medium">Montant</Label>
                  <p className="text-sm font-bold text-green-600">
                    {formatPrice(parseFloat(selectedRevenue.amount), settings?.currency)}
                  </p>
                </div>
                <div>
                  <Label className="text-sm font-medium">Date</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400">
                    {format(new Date(selectedRevenue.revenueDate), 'dd/MM/yyyy', { locale: fr })}
                  </p>
                </div>
                <div>
                  <Label className="text-sm font-medium">Mode de paiement</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400 capitalize">
                    {selectedRevenue.paymentMethod.replace('_', ' ')}
                  </p>
                </div>
              </div>
              <div>
                <Label className="text-sm font-medium">Description</Label>
                <p className="text-sm text-gray-600 dark:text-gray-400">{selectedRevenue.description}</p>
              </div>
              {selectedRevenue.source && (
                <div>
                  <Label className="text-sm font-medium">Source</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400">{selectedRevenue.source}</p>
                </div>
              )}
              {selectedRevenue.notes && (
                <div>
                  <Label className="text-sm font-medium">Notes</Label>
                  <p className="text-sm text-gray-600 dark:text-gray-400">{selectedRevenue.notes}</p>
                </div>
              )}
            </div>
          )}
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => selectedRevenue && printRevenue(selectedRevenue as Revenue & { category: RevenueCategory })}
              data-testid="button-print-revenue"
            >
              <FileText className="w-4 h-4 mr-2" />
              Imprimer
            </Button>
            <Button onClick={() => setIsDetailsDialogOpen(false)} data-testid="button-close-details">
              Fermer
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

