import {
  pgTable,
  text,
  varchar,
  timestamp,
  jsonb,
  index,
  boolean,
  integer,
} from "drizzle-orm/pg-core";

// Session storage table - required for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table - for local authentication
export const users = pgTable("users", {
  id: varchar("id").primaryKey().notNull(),
  email: varchar("email").unique().notNull(),
  password: varchar("password"), // For local auth
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),

  phone: varchar("phone", { length: 50 }),
  company: varchar("company", { length: 255 }),
  position: varchar("position", { length: 255 }),
  address: text("address"),
  businessType: varchar("business_type", { length: 255 }),
  currency: varchar("currency", { length: 10 }).default("XOF"), // XOF ou GHS
  language: varchar("language", { length: 10 }).default("fr"), // fr ou en
  licenseActivated: boolean("license_activated").default(false), // Licence activ√©e pour cet utilisateur

  // Champs pour la s√©curit√© et la r√©initialisation de mot de passe
  loginAttempts: integer("login_attempts").notNull().default(0), // Nombre de tentatives de connexion √©chou√©es
  lockUntil: timestamp("lock_until"), // Date jusqu'√† laquelle le compte est verrouill√©
  resetPasswordTokenHash: varchar("reset_password_token_hash", { length: 64 }).unique(), // Hash du token de r√©initialisation (s√©curis√©)
  resetPasswordExpires: timestamp("reset_password_expires"), // Expiration du token de r√©initialisation

  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

